mapper.select:
SELECT gid, COUNT(*) AS shortLinkCount FROM t_link WHERE gid IN
#{gid}
AND enable_status = 1 AND del_flag = 0 GROUP BY gid

mapper.update:
UPDATE t_link
SET total_pv = total_pv + #{totalPv},
    total_uv = total_uv + #{totalUv},
    total_uip = total_uip + #{totalUip}
WHERE gid = #{gid}
  AND full_short_url = #{fullShortUrl}
